import {
    LOGIN_USER,
    LOGIN_USER_ERROR,
    LOGIN_USER_SUCCESS,
    LOGIN_USER_ERROR_FIELD,
} from "../actions/loginAction";

import { CLEAR_USER_INFORMATION } from '../actions/logoutAction'


const DEFAULT_STATE = {
  status: 'initialState',
  user:'',
  token:''
};

export default function (state = DEFAULT_STATE, action) {
    switch (action.type) {
      case LOGIN_USER:
      return Object.assign({}, state, {
        status: 'loading'
      })
      case LOGIN_USER_SUCCESS:
      return {status:'Online',user:action.data.user,token:action.data.token}
      case LOGIN_USER_ERROR:
      return {
        status: 'error',
        error: action.error,
      }
      case LOGIN_USER_ERROR_FIELD:
      return {
        status: 'errorField',
        error: action.errorField,
      }
      case CLEAR_USER_INFORMATION:
        return DEFAULT_STATE
      default:
        return state;
    }
}
