import React from 'react';
import { Field} from 'redux-form';
import TextField from '@material-ui/core/TextField';
import Grid from '@material-ui/core/Grid';
const renderField = (
    {type, input, label, meta : { touched, error}}
    ) => (
        <TextField
            {...input}
            type = {type}
            label = {label}
            error = {touched && error}
            helperText={touched && error}
            variant="outlined"
            fullWidth
        />
)
const RadioGroup = (props) => {
    const { input, meta, options } = props;
    const hasError = meta.touched && meta.error;

    return (
      <div>
        {options.map(o => <label key={o.value}>
          <Radio {...input}  checked={o.value === input.value} value={o.value} />
          {o.title}</label>)}<br/>
        {hasError && <span style={{'fontSize':'12px','color':'#f44336'}}>{meta.error}</span>}
      </div>
    );
}
 const profileInfo = () => {
    return (
    <div>
        <form>
            <Grid container spacing={2}>
            <Grid item xs={12} sm={6}>
                <Field
                    name="firstname"
                    component={renderField}
                    label="Firstname"
                    type = "text"
                />
            </Grid>
            <Grid item xs={12} sm={6}>
                <Field
                    name="lastname"
                    component={renderField}
                    label="Lastname"
                    type = "text"
                />
            </Grid>
            <Grid item xs={12}>
                <Field
                    name="username"
                    component={renderField}
                    label="Username"
                    type = "text"

                />
            </Grid>
            <Grid item xs={12}>
                <Field
                    name="email"
                    component={renderField}
                    label="Email"
                    type = "email"
                />
            </Grid>
            </Grid>
        </form>
    </div>
    )
}
export default profileInfo;
