import React, {useEffect} from "react";
import { SnackbarProvider, useSnackbar } from "notistack";

function MyFlash() {
    //const {msg, variant} = props;
    const { enqueueSnackbar } = useSnackbar();
    const msg = ['hi', 'hello'];
    useEffect(() => {
        msg.forEach(element => {
            enqueueSnackbar(element, { variant: 'success' });
         });
    }, [])
    return('');
}

export default function Snackbar(){
    return(
        <SnackbarProvider
            anchorOrigin={{
                vertical: 'top',
                horizontal: 'right',
            }}
            autoHideDuration={4000}
            maxSnack={4}
        >
        <MyFlash />
        </SnackbarProvider>
    );
};
