import {
  INSCRIPTION_USER,
  INSCRIPTION_USER_ERROR,
  INSCRIPTION_USER_SUCCESS
} from "../actions/registerAction";
const DEFAULT_STATE = {
    "loadingStatus" : "idl"
};
export default function (state = DEFAULT_STATE, action) {
  switch (action.type) {
  case INSCRIPTION_USER:
    return state.merge("loadingStatus", "loading");
  case INSCRIPTION_USER_SUCCESS:
    return state.
    merge("loadingStatus", "success");
  case INSCRIPTION_USER_ERROR:
    return state.merge("loadingStatus", "error");
  default:
    return state;
  }
}
