import React from "react";
import { SnackbarProvider, useSnackbar } from "notistack";

function MyFlash() {
    //const {msg, variant} = props;
    const { enqueueSnackbar } = useSnackbar();
    const msg = ['hi', 'hello'];
    //msg.forEach(element => {
    //    enqueueSnackbar(element, { variant: 'success' });
    //});

    return(<>{enqueueSnackbar('hi', { variant: 'success' })}</>);
}

export default function Snackbar(){
    return(
        <SnackbarProvider
            anchorOrigin={{
                vertical: 'top',
                horizontal: 'right',
            }}
            autoHideDuration={4000}
            maxSnack={4}
        >
        <MyFlash />
        </SnackbarProvider>
    );
};
