import {put, takeLatest} from "redux-saga/effects";
import {push} from "react-router-redux";
import { select } from 'redux-saga/effects'; 
import {request} from './helper';
import { updateUserSuccess} from '../actions/userAction';
export const Stepper =
    function *Stepper () {
        try {
            console.log('dasdasdas')
            const user = yield select(state => state.user);
            const response = yield call(request, {
                "url": "http://localhost:5000/updateStep",
                "data": {step : user.complete + 1,id : user.id},
                "method": "post"
              });
              yield put(updateUserSuccess(response.data.user));
        } catch (error) {
            console.log(error);
        }
    };

export default function *() {
    yield takeLatest("INC_STEPPER", Stepper);
}
