import {
    LOGIN_USER,
    LOGIN_USER_ERROR,
    LOGIN_USER_SUCCESS
} from "../actions/loginAction";

export default function (state = {status: 'initialState'}, action) {
    switch (action.type) {
    case LOGIN_USER:
    return state.set("status","loading");
    case LOGIN_USER_SUCCESS:
      return state.set("status","success") &&  state.set("data", action.data);
    case LOGIN_USER_ERROR:
      return state.set("status","error") && state.set("error", action.error);
    default:
      return state;
    }
}
