import { takeLatest, put } from "redux-saga/effects";
import { getOptionsSuccess, createOptionSuccess, createOptionError} from "../actions/addInfoAction";
import axios from 'axios';

const getSelectOptions =
  function *getSelectOptions () {
    try {
        const response  = yield axios.post('http://localhost:5000/getOptions');
        if(response.data)
        {
            yield put(getOptionsSuccess(response.data));
        }
        else
        {
            yield put(getOptionsSuccess());
        }
    }catch (error) {
      if (error.response) {
        yield put(getOptionsSuccess());
      }
    }
};

const createSelectOption =
  function *createSelectOption () {
    try {
        const response  = yield axios.post('http://localhost:5000/createOption');
        if(response.data)
        {
            yield put(createOptionSuccess(response.data));
        }
        else
        {
            yield put(createOptionError());
        }
    }catch (error) {
      if (error.response) {
        yield put(createOptionError([]));
      }
    }
};

export default function *() {
  yield takeLatest("GET_OPTIONS", getSelectOptions);
  yield takeLatest("CREATE_OPTION", createSelectOption);
}
