import {GET_USER_DATA,GET_USER_DATA_SUCCESS} from "../actions/addInfoAction";



const DEFAULT_STATE =  0;

export default function (state = DEFAULT_STATE, action) {
    switch (action.type) {
        case GET_USER_DATA:
            return { getData: 'loading'};
        case GET_USER_DATA_SUCCESS:
            return { getData: 'success'};
        default:
            return state;
    }
}
