import {
    SEND_EMAIL,
    SEND_EMAIL_SUCCESS,
    SEND_EMAIL_ERROR
} from "../actions/sendEmail";

const DEFAULT_STATE = {
  status: 'initialState',
};

export default function (state = DEFAULT_STATE, action) {
    switch (action.type) {
      case SEND_EMAIL:
        return {status: "loading"}
      case SEND_EMAIL_SUCCESS:
      return {status: "success"}
      case SEND_EMAIL_ERROR:
        return {status: "error"}
      default:
        return state;
    }
}
