import {takeLatest, put} from "redux-saga/effects";
import { ResetPasswordSuccess, ResetPasswordError } from "../actions/resetPasswordAction";
import axios from 'axios';

const resetPass =
  function *resetPass ({data}) {
    try {
        console.log(data.form);
      const response = yield axios.post('http://localhost:5000/resetPassword', {email: data.data.email});
      console.log(response.data);
      if(response.data.sent)
      {
        yield put(ResetPasswordSuccess());
      }
      else if(response.data.error === 'Email not found')
      {
        yield put(ResetPasswordError('Email not found'));
      }
    }catch (error) {
      if (error.response) {
        yield put(ResetPasswordError('Error sending the email, please retry'));
      }
    }
  };

export default function *() {
  yield takeLatest("RESET_PASSWORD", resetPass);
}
