import React  from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { Grid, Container } from '@material-ui/core';
import AddAPhotoIcon from '@material-ui/icons/AddAPhoto';
import IconButton from '@material-ui/core/IconButton';
import img from '../../public/images/2019-12-10T00:14:23.376Z655568.jpg';
import InfoIcon from '@material-ui/icons/Info';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import GridListTileBar from '@material-ui/core/GridListTileBar';
import ListSubheader from '@material-ui/core/ListSubheader';
import HighlightOffTwoToneIcon from '@material-ui/icons/HighlightOffTwoTone';
import AddToPhotosTwoToneIcon from '@material-ui/icons/AddToPhotosTwoTone';
import FormControlLabel from '@material-ui/core/FormControlLabel';
//import Button from '@material-ui/core/Button';

const useStyles = makeStyles(theme => ({
  card: {
   height :150,
    position : 'center',
  },
  input: {
    display: 'none',
  },
  submit: {
    margin: theme.spacing(2, 0, 2),
    backgroundColor: theme.palette.secondary.main,
}, root: {
  display: 'flex',
  flexWrap: 'wrap',
  justifyContent: 'space-around',
  overflow: 'hidden',

},
gridList: {
  width: 500,
  height: 450,
},
titleBar: {
  background:
    'linear-gradient(to top, rgba(0,0,0,0.6) 0%, ' +
    'rgba(0,0,0,0.3) 70%, rgba(0,0,0,0) 100%)',
},
delete : {
  background : 'none'
}

}));
const GreenRadio = withStyles({
  root: {
    color: green[400],
    '&$checked': {
      color: green[600],
    },
  },
  checked: {},
})(props => <Radio color="default" {...props} />);
const  Pictures = (props) => {
  const {fileChangedHandler,imagePreviewUrl,images} = props;
  const classes = useStyles();
  // const fun = ()=>{
  //   const url =  'http://localhost:5000/images/2019-12-10T00:14:23.376Z655568.jpg'
  //   console.log(url);
  //   return(url);
  // }
  return (
    <Container>
      <Grid container  justify="center" width>
        <input accept="image/*" className={classes.input} id="icon-button-file" type="file"  onChange={fileChangedHandler}/>
         <label htmlFor="icon-button-file">
          <IconButton color="primary" aria-label="upload picture" component="span">
            <AddAPhotoIcon color='secondary' style={{fontSize : 70}}/>
          </IconButton>
        </label>
      </Grid>
      { imagePreviewUrl && <img src={imagePreviewUrl} alt="icon" width="200" />}
      {/* {images.images && images.images.map(item =>{
       console.log(item.path)
        return (<img src={img} alt="icon" width="200" />) */}

          <div className={classes.root}>
            <GridList cellHeight={180} className={classes.gridList}>
              {images.images && images.images.map(tile => (
              <GridListTile>
                <img src={img} alt='photos' />
                <GridListTileBar
                actionPosition="left"
                className={classes.titleBar}
                title='Profile picture'
                  actionIcon={
                  <IconButton >
                    <AddToPhotosTwoToneIcon color='primary' />
                  </IconButton>
                  }
                />
                <GridListTileBar
                className={classes.delete}
                titlePosition="top"

                  actionIcon={
                    <GreenRadio
                      //checked={selectedValue === 'c'}
                      //onChange={handleChange}
                      value="c"
                      name="radio-button-demo"
                      inputProps={{ 'aria-label': 'C' }}
                    />
                  }
                />
              </GridListTile>
              ))}
            </GridList>
          </div>
    </Container>
  )
}

export default Pictures;
