import React from 'react'
import { Redirect } from 'react-router-dom';
import {connect} from "react-redux";
import {Route, Switch} from 'react-router-dom'
import RegisterContainer from '../containers/Register'
import HomeContainer from '../containers/Home'
import loginContainer from '../containers/Login'
import NotFoundPage from '../containers/NotFoundPage'
import ForgotPasswordContainer from '../containers/sendEmail'
import EmailConfirmCont from '../containers/emailConfirmation'
import ResetPasswordContainer from '../containers/resetPassword'
import CompleteProfile from '../containers/completeProfile/stepper'
import Profile from '../containers/Profile'
import PrivateRoute from './PrivateRoute';

const Routes = (props) => {
    const {user} = props
    return (
        <>
            <Switch>
                {user === null ? <Route path="/confirmation/:token"  component={ EmailConfirmCont }/> : <Redirect to={'/'} />}
                {user === null ? <Route path="/resetPassword/:token"  component={ ResetPasswordContainer }/> : <Redirect to={'/'} />}
                {user === null ? <Route path="/forgotPassword"  component={ ForgotPasswordContainer }/> : <Redirect to={'/'} />}
                {user === null ? <Route path="/register"  component={ RegisterContainer } /> : <Redirect to={'/'} />}
                {user === null ? <Route path="/login"  component={ loginContainer }/> : <Redirect to={'/'} />}
                <PrivateRoute>
                    <Route path="/completeProfile"  component={ CompleteProfile }/>
                    <Route path="/" component={ HomeContainer }/>
                    <Route path="/profile" component={ Profile }/>
                </PrivateRoute>
                <Route  path="*" component={ NotFoundPage } />
            </Switch>
        </>
    )
}
const mapStateToProps = (state) => (
{
    'user': state.user,
});
export default connect(mapStateToProps)(Routes);
