import React ,{useState} from 'react';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/core';

const useStyles = makeStyles(theme => ({
    container: {

    },
    textField: {
      marginLeft: theme.spacing(1),
      marginRight: theme.spacing(1),
    }
}))



const Register = (props) => {
    const classes = useStyles();

    const [dataUser, setData] = useState({
        Firstname : '',
        Lastname : '',
        Username : '',
        Email : '',
        Password : '',
        ConfirmPassword : ''
    })
    const handleSubmit = (e) =>{
                e.preventDefault();
                console.log(dataUser)
            }

            const handleChange = (e) => {
            setData({ ...dataUser, [e.target.name]: e.target.value });
        }    
        const {lastname, firstname, username, email, password, confirmPassword} = dataUser;
        return (
            <div>
                <h1>Register</h1>
                <form className={classes.container}>
                    <Input name = "Firstname" handleChange = {handleChange} data = {firstname}/>
                    <Input name = "Lastname"  handleChange = {handleChange} data = {lastname}/>
                    <Input name = "Username" handleChange = {handleChange} data = {username}/>

                    <Input name = "Email" handleChange = {handleChange} data = {email}/>
                    <Input name = "Password" handleChange = {handleChange} data = {password}/>
                    <Input name = "ConfirmPassword" handleChange = {handleChange} data = {confirmPassword}/>

                    <Button type="submit" variant="outlined" color="primary" onSubmit={e => handleSubmit(e)}>Register</Button>
                </form>


            </div>
        )


}

const Input = (props) => {
    const classes = useStyles();

    return (
        <div>
            <TextField
                id={props.name}
                label={props.name}
                value = {props.data}
                name = {props.name}
                margin="dense"
                variant="outlined"
                rowsMax="4"
                className={classes.textField}
                onChange={e => props.handleChange(e)}
            /> 
        </div>
    )
}



export default Register;
