import React from 'react'
import {connect} from "react-redux";
import {reduxForm} from 'redux-form';
import ForgotPassword from '../../components/ForgotPassword'

const validate = (values) => {
    const errors = {};

    const requiredFields = [
        'email'
      ];
      requiredFields.forEach(field => {
        if (!values[field]) {
          errors[field] = 'Required!';
        }
      });

    if (values.email && !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(values.email)) {
        errors.email = "Invalid email address";
    }
    return errors;
}

const submit = values => {
    window.alert (JSON.stringify (values));
};

const forgotPasswordContainer = () => {
    return (
        <div>
            <ForgotPassword
                onSubmit={submit}
            />
        </div>
    )
}
const mapStateToProps = (state) => (
{
    "form" : state.form,
    "status" : state.login.status,
    "errors": state.login.error,
    "token" : state.login.token,
    "registredStatus": state.register
});
const mapDispatchToProps = {
    "sendEmail": SendEmail
};
const mergeProps = (stateProps, dispatchProps, otherProps) => ({
    ...stateProps,
    ...dispatchProps,
    ...otherProps,
    "handleSubmit" : otherProps.handleSubmit((form) => {
        dispatchProps.loginAction(form);
    })
});


const cnForgotPasswordContainer = connect(mapStateToProps, mapDispatchToProps,mergeProps)(ForgotPassword);
const ForgotPasswordContainer = reduxForm ({
    form: 'ForgotPassword',
    validate
}) (cnForgotPasswordContainer);

export default ForgotPasswordContainer;
