import {
    RESET_PASSWORD,
    RESET_PASSWORD_SUCCESS,
    RESET_PASSWORD_ERROR
} from "../actions/resetPasswordAction";

const DEFAULT_STATE = {
  status: 'initialState',
};

export default function (state = DEFAULT_STATE, action) {
    switch (action.type) {
      case RESET_PASSWORD:
        return "loading"
      case RESET_PASSWORD_SUCCESS:
      return "success"
      case RESET_PASSWORD_ERROR:
        return "error"
      default:
        return state;
    }
}
