import {
    DECODE_TOKEN,
    DECODE_TOKEN_SUCCESS,
    DECODE_ERROR
  } from "../actions/decodeAction";

  export default function (state = {status: 'initialStatus',  user: null}, action) {
    switch (action.type) {
    case DECODE_TOKEN:
      return {status: "loading"}
    case DECODE_TOKEN_SUCCESS:
      return {status: "success", user: action.data};
    case DECODE_ERROR:
      return {status: "error"};
    default:
      return state;
    }
  }
