import {decodeTokenAction} from '../actions/decodeAction';
import {connect} from "react-redux";
import ls from 'local-storage';

export const decodeToken = (props) => {
    return props.decodeToken;
}

const mapStateToProps = (state) => ({
    "status" : state.status
});
const mapDispatchToProps = {
    "decodeTokenAction": decodeTokenAction
};
const mergeProps = (stateProps, dispatchProps, otherProps) => ({
    ...stateProps,
    ...dispatchProps,
    ...otherProps,
    "decodeToken" : () => {
        const token = ls.get('token');
        dispatchProps.decodeTokenAction(token);
    }
});

export default connect(mapStateToProps, mapDispatchToProps,mergeProps)(decodeToken);
