import axios from 'axios'
const sleep = ms => new Promise(resolve => setTimeout(resolve, ms))

const asyncValidate = (values) => {
  return sleep(1000)
    .then(async () => {
      const error = {}
      const er = await axios.post('http://localhost:5000/register', values)
      console.log(er.data)
      if ([ 'isifeddi' ].includes(values.username)) {
        error.username = 'Username already taken' 
      }
      if ([ 'sifeddineilyass@gmail.com' ].includes(values.email)) {
        error.email= 'Email already taken'
      }
      if(error.username !== 'undefined' && error.email !== 'undefined')
        throw error
    })
}

export default asyncValidate
