import React, {useEffect}  from 'react';
import CssBaseline from '@material-ui/core/CssBaseline';
import CheckCircleIcon from '@material-ui/icons/CheckCircle';
import { Link } from 'react-router-dom';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import CircularProgress from '@material-ui/core/CircularProgress';
import {EmailConfirmationAction} from '../../actions/emailConfirmation';
import {connect} from "react-redux";


const useStyles = makeStyles(theme => ({

  paper: {
    marginTop: theme.spacing(8),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
}));

const EmailConfirmation = (props) => {

  useEffect((props) => {
    console.log('props', props);
    const token = props.match.params;
    props.emailConfirm(token);
  }, []);

  const classes = useStyles();
  return (
    <Container component="main" maxWidth="xs">
      <CssBaseline />

    {props.status !== 'loading' && <div className={classes.paper}>
        <Typography variant="h3" color="primary">
          Email confirmation
        </Typography>
        {props.status === 'success' && <Typography variant="h2" color="success">
          <CheckCircleIcon /> Email verified successfully.
          <br/>
          You can now <Link to="/login"  style={{textDecoration: 'none', color:'#3f51b5'}}>Login</Link>
        </Typography>}

        {props.status === 'error' && <Typography variant="h2" color="error.main">
          <CheckCircleIcon /> There was an error, please retry.
        </Typography>}        
      </div>}
      {props.status === "loading" && <div className={classes.paper} style={{marginTop: "300px"}}><CircularProgress color="secondary" /></div>}

    </Container>
  );
}

const mapStateToProps = (state) => (
  {
      "status" : state.emailConfirmation.status,
  });
  const mapDispatchToProps = {
      "emailConfirmation": EmailConfirmationAction
  };
  const mergeProps = (stateProps, dispatchProps, otherProps) => ({
      ...stateProps,
      ...dispatchProps,
      ...otherProps,
      "emailConfirm" : function (token){
          dispatchProps.emailConfirmation(token);
      }
  });

export default connect(mapStateToProps, mapDispatchToProps,mergeProps)(EmailConfirmation);
