import React from 'react';
import { Field} from 'redux-form';
import TextField from '@material-ui/core/TextField';
import Grid from '@material-ui/core/Grid';
import Radio from '@material-ui/core/Radio';
import FormLabel from '@material-ui/core/FormLabel';
import Button from '@material-ui/core/Button';
import CreatableSelect from 'react-select/creatable';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({

    paper: {
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        width: '100%'
    },
    form: {
        width: '100%',
        marginTop: theme.spacing(3),
    },
    submit: {
        margin: theme.spacing(2, 0, 2),
        backgroundColor: theme.palette.secondary.main,
    },
}));

const renderField = (
    {type, input, label, meta : { touched, error}}
    ) => (
        <TextField
            {...input}
            type = {type}
            label = {label}
            error = {touched && error}
            helperText={touched && error}
            variant="outlined"
            fullWidth
        />
)
const RadioGroup = (props) => {
    const { input, meta, options } = props;
    const hasError = meta.touched && meta.error;

    return (
      <div>
        {options.map(o => <label key={o.value}>
          <Radio {...input}  checked={o.value === input.value} value={o.value} />
          {o.title}</label>)}<br/>
        {hasError && <span style={{'fontSize':'12px','color':'#f44336'}}>{meta.error}</span>}
      </div>
    );
}
const renderDatepicker = ({input, label, meta : { touched, error}}
    ) => (
        <TextField
            {...input}
            type = 'date'
            label = {label}
            error = {touched && error}
            helperText={touched && error}
            fullWidth
            InputLabelProps={{
              shrink: true,
            }}
        />
  )
  const sel = ({ input, meta: { touched, error }}) => (
    <div>
      <CreatableSelect
        {...input}
        isMulti
        //isDisabled={selectLoading}
        //isLoading={selectLoading}
        isClearable={false}
        //options={selectOptions}
        onBlur={() => input.onBlur(input.value)}
        onChange={(value) => { input.onChange(value) }}
        //onCreateOption={handleCreate}
      />
      <div>{(touched && error) &&
        <div style={{'fontSize':'12px','color':'rgb(244, 67, 54)'}}>{error}</div>}
      </div>
    </div>
  );
 const ProfileInfo = () => {
    const classes = useStyles();
    return (
    <div>
        <form>
            <Grid container justify="center" spacing={2}>
            <Grid item xs={5} >
            <FormLabel  component="legend">Firstname</FormLabel>
                <Field
                    name="firstname"
                    component={renderField}
                    type = "text"
                />
            </Grid>
            <Grid item xs={5}>
            <FormLabel  component="legend">Lastname</FormLabel>
                <Field
                    name="lastname"
                    component={renderField}
                    type = "text"
                />
            </Grid>
            <Grid item xs={5}>
            <FormLabel  component="legend">Username</FormLabel>
                <Field
                    name="username"
                    component={renderField}
                    type = "text"
                />
            </Grid>
            <Grid item xs={5}>
            <FormLabel  component="legend">Email</FormLabel>
                <Field
                    name="email"
                    component={renderField}
                    type = "email"
                />
            </Grid>
            <Grid  xs={10}>

              <FormLabel  component="legend">Bio</FormLabel>
              <Field
                name="bio"
                component={renderField}
                type = "text"
                rows='4'
                variant='outlined'
              />
            </Grid>

            <Grid item xs={5}>
              <FormLabel component="legend">Gender</FormLabel>
              <Field component={RadioGroup} name="gender" required={true} options={[
                    { title: 'Male', value: 'male' },
                    { title: 'Female', value: 'female' }
                  ]}
              />
            </Grid>
            <Grid item xs={5}>
            <FormLabel component="legend">Match with</FormLabel>
              <Field component={RadioGroup} name="sexOrient" required={true} options={[
                    { title:  'Men ' , value: 'men'  },
                    { title:  'Women', value: 'women'},
                    { title:  'Both' , value: 'both' }
                  ]}
              />
            </Grid>
            <Grid item xs={5}>
              <FormLabel component="legend">Birthday</FormLabel>
              <Field
                name="birthday"
                component={renderDatepicker}
              />
            </Grid>
            <Grid item xs={5}>
              <FormLabel component="legend">Interests</FormLabel>
              <Field name='interests' component={sel}/>
            </Grid>


              <Grid item container justify='center' xs={3}>
                <Button  className={classes.submit} fullWidth variant="contained" type="submit" color="primary" name="submit" value="ok" >Next</Button>
              </Grid>
            </Grid>
        </form>
    </div>
    )
}
export default ProfileInfo;
