import {
    DECODE_TOKEN,
    DECODE_TOKEN_SUCCESS,
    DECODE_ERROR
  } from "../actions/decodeAction";

  export default function (state = {user: null}, action) {
    switch (action.type) {
    case DECODE_TOKEN:
      return {user: null}
    case DECODE_TOKEN_SUCCESS:
      return {user: action.data};
    case DECODE_ERROR:
      return {user: null};
    default:
      return state;
    }
  }
