import React, { Component } from 'react';
import Localisation from '../../components/completeProfile/localisation';
import {connect} from "react-redux";

class LocalisationContainer extends Component{
    state = { userLocation: { lat: 32, lng: 32 }, loading: true};
    componentDidMount(){
        const options = {
            enableHighAccuracy: true,
            timeout: 5000,
            maximumAge: 0
        };
        navigator.geolocation.getCurrentPosition(
            position => {
                const { latitude, longitude, accuracy } = position.coords;
                this.setState({
                    userLocation: { lat: latitude, lng: longitude },
                    loading: false
                });
                console.log('Votre position actuelle est :');
                console.log(`Latitude : ${latitude}`);
                console.log(`Longitude : ${longitude}`);
                console.log(`La précision est de ${accuracy} mètres.`);
            },
            err => {
                this.setState({
                    userLocation: { lat: 32.8781004, lng: -6.8887306 },
                    loading: false
                });
                console.warn(`ERREUR (${err.code}): ${err.message}`);
            },
            options
        );
        //axios.get('http://ipinfo.io/json').then(res => console.log(res)).catch(err => console.log(err));
    }
    render(){
        const {loading, userLoc} = this.state;
        const userLocation = {lat: parseFloat(this.props.user.lat), lng: parseFloat(this.props.user.lng)}
        if(loading) return null;
        if(userLoc.lat === 0)
            return (
            <Localisation userL={userLocation}/>
            )
        else
            return (
            <Localisation userL={userLoc}/>
            )
    }
}
const mapStateToProps = (state) => (
{
    "user": state.user,
});

export default connect(mapStateToProps)(LocalisationContainer);
