import React from 'react';
import { Field} from 'redux-form';
import TextField from '@material-ui/core/TextField';
import Grid from '@material-ui/core/Grid';
const renderField = (
    {type, input, label, meta : { touched, error}}
    ) => (
        <TextField
            {...input}
            type = {type}
            label = {label}
            error = {touched && error}
            helperText={touched && error}
            variant="outlined"
            fullWidth
        />
)
 const profileInfo = () => {
    return (
    <div>
        <form>
            <Grid container spacing={2}>
            <Grid item xs={12} sm={6}>
                <Field
                    name="firstname"
                    component={renderField}
                    label="Firstname"
                    type = "text"
                />
            </Grid>
            <Grid item xs={12} sm={6}>
                <Field
                    name="lastname"
                    component={renderField}
                    label="Lastname"
                    type = "text"
                />
            </Grid>
            <Grid item xs={12}>
                <Field
                    name="username"
                    component={renderField}
                    label="Username"
                    type = "text"

                />
            </Grid>
            <Grid item xs={12}>
                <Field
                    name="email"
                    component={renderField}
                    label="Email"
                    type = "email"
                />
            </Grid>
            </Grid>
        </form>
    </div>
    )
}
export default profileInfo;
