import {
    LOGIN_USER,
    LOGIN_USER_ERROR,
    LOGIN_USER_SUCCESS,

} from "../actions/loginAction";

export default function (state = {status: 'initialState'}, action) {
    switch (action.type) {
    case LOGIN_USER:
      return ("loading");
    case LOGIN_USER_SUCCESS:
      return ("success")
    case LOGIN_USER_ERROR:
      return ("error")
    default:
      return state;
    }
}
