import {decodeTokenAction} from '../actions/decodeAction';
import {connect} from "react-redux";
import ls from 'local-storage';

export const decodeToken = (props) => {
    return props.decodeToken;
}

const mapStateToProps = (state) => ({
    "status" : state.status
});
const mapDispatchToProps = dispatch => ({
    decodeTokenAction: () => dispatch(decodeTokenAction)
});

export default connect(mapStateToProps, mapDispatchToProps)(decodeToken);
