import {
    LOGIN_USER,
    LOGIN_USER_ERROR,
    LOGIN_USER_SUCCESS,
    LOGIN_USER_ERROR_USERNAME,
    LOGIN_USER_ERROR_PASSWORD
} from "../actions/loginAction";

export default function (state = {status: 'initialState'}, action) {
    switch (action.type) {
    case LOGIN_USER:
      return state.set({status:"loading"});
    case LOGIN_USER_SUCCESS:
      return state.set("status","success")
    case LOGIN_USER_ERROR:
      return state.set("status","error")
      case LOGIN_USER_ERROR_USERNAME:
      return state.set("status","Username Not Found")
      case LOGIN_USER_ERROR_PASSWORD:
      return state.set("status","Password incorrect")
    default:
      return state;
    }
}
