import {
    GET_USERS,
    GET_USERS_SUCCESS,
    GET_USERS_ERROR,

} from "../actions/userAction";

export default function (state = null, action) {
    switch (action.type) {
        case GET_USERS:
            return 'loading';
        case GET_USERS_SUCCESS:
            return action.data;
        case GET_USERS_ERROR:
            return {err : action.err};
      default:
        return state;
    }
}
