import React, { Component } from 'react';
import Stepper from '../../components/completeProfile/stepper';
import {getOptions} from '../../actions/addInfoAction';
import {getActiveStep,incStepper,decStepper} from '../../actions/stepperAction';
import {getImages} from '../../actions/imagesAction';
import {connect} from "react-redux";

class StepperContainer extends Component {
    componentDidMount = () => {
        if(this.props.user){
            this.props.getOptions();
            this.props.getImages(this.props.user.id);
            this.props.getActiveStep(this.props.user.id);
        }
    }
    render() {
        return (
            <Stepper activeStep={this.props.activeStep} incStepper={incStepper} decStepper={decStepper}/> 
        )
    }
}

const mapStateToProps = (state) => (
{
    "activeStep" : state.activeStep,
    "user": state.user
});
const mapDispatchToProps = {
    "getOptions": getOptions,
    "getActiveStep": getActiveStep,
    "getImages" : getImages,
    "incStepper" : incStepper,
    "decStepper" : decStepper,
};
const mergeProps = (stateProps, dispatchProps, otherProps) => ({
    ...stateProps,
    ...dispatchProps,
    ...otherProps,

    "handleBack" : otherProps.handleSubmit(() => {
        dispatchProps.decStepper();
    }),
    "handleNext" : otherProps.handleSubmit(() => {
        dispatchProps.incStepper();
    }),
});


export default connect(mapStateToProps, mapDispatchToProps,mergeProps)(StepperContainer);
