import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import clsx from 'clsx';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import Collapse from '@material-ui/core/Collapse';
import Avatar from '@material-ui/core/Avatar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import { red } from '@material-ui/core/colors';
import FavoriteIcon from '@material-ui/icons/Favorite';
import ShareIcon from '@material-ui/icons/Share';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import MoreVertIcon from '@material-ui/icons/MoreVert';
import Box from '@material-ui/core/Box';
import Rating from '@material-ui/lab/Rating';
import img from '../../public/images/2019-12-10T00:14:23.376Z655568.jpg';
  import ReactIdSwiperCustom from 'react-id-swiper/lib/ReactIdSwiper.custom';
  import { Swiper, Navigation, Pagination } from 'swiper/js/swiper.esm';

const useStyles = makeStyles(theme => ({
  card: {
    maxWidth: 500,
    height:600,
  },

  expand: {
    transform: 'rotate(0deg)',
    marginLeft: 'auto',
    transition: theme.transitions.create('transform', {
      duration: theme.transitions.duration.shortest,
    }),
  },
  expandOpen: {
    transform: 'rotate(180deg)',
  },
  avatar: {
    backgroundColor: '#00FB0C',
    width: 15,
    height: 15,



  },
}));

export default function RecipeReviewCard() {
  const classes = useStyles();
  const [expanded, setExpanded] = React.useState(false);
  const value = 5;
  const [hover, setHover] = React.useState(-1);
  const handleExpandClick = () => {
    setExpanded(!expanded);
  };
  const params = {
    Swiper,
    modules: [Navigation, Pagination],
    pagination: {
      el: '.swiper-pagination',
      type: 'bullets',
      clickable: true,
    },
    navigation: {
      nextEl: '.swiper-button-next',
      prevEl: '.swiper-button-prev'
    },
    spaceBetween: 30
  }
  const images = [
        'images/slide_2.jpg',
        'images/slide_3.jpg',
        'images/slide_4.jpg',
        'images/slide_5.jpg',
        'images/slide_6.jpg',
        'images/slide_7.jpg'
      ];
      const labels = {
        0.5: 'Useless',
        1: 'Useless+',
        1.5: 'Poor',
        2: 'Poor+',
        2.5: 'Ok',
        3: 'Ok+',
        3.5: 'Good',
        4: 'Good+',
        4.5: 'Excellent',
        5: 'Excellent+',
      };

  return (
    <Card className={classes.card}>
      <CardHeader
      action={ 
        <Box component="fieldset" mb={3} borderColor="transparent">
        <div className={classes.rating1}>
        <Box ml={2}>{labels[hover !== -1 ? hover : value]}</Box>
          <Rating
            name="hover-side"
            value={value}
            precision={0.5}
            onChangeActive={(event, newHover) => {
              setHover(newHover);
            }}
          />

        </div>
        </Box>

        }
        avatar={
          <Avatar aria-label="recipe" className={classes.avatar}>

          </Avatar>

        }
        title="Monica Bellucci 55"
        subheader=" Online"
      >

      </CardHeader>

      <CardMedia
        children={
            <ReactIdSwiperCustom {...params}>
                {
                    images.map((each) =>
                    <div>
                        <img  style={{width: "100%"}} src={img} />
                    </div>
                    )
                }
                </ReactIdSwiperCustom>
        }
        title="Paella dish"
      />
      <CardContent>
        <Typography >
          <strong>BIO :</strong> This impressive paella is a perfect party dish and a fun meal to cook . 
        </Typography>
        <Typography>
        <strong>TAGS :</strong> Sport 1337 LOVE .
        </Typography>
      </CardContent>

      <CardActions disableSpacing>
        <IconButton aria-label="add to favorites">
          <FavoriteIcon />
        </IconButton>
        <IconButton aria-label="share">
          <ShareIcon />
        </IconButton>
      </CardActions>
    </Card>
  );
}
