import {takeLatest, put,push} from "redux-saga/effects";
// import {push} from "react-router-redux";
import {loginError, loginUserSuccess,loginErrorField} from "../actions/loginAction";
import {getImages} from '../actions/imagesAction'
import {decodeTokenAction} from '../actions/decodeAction';
import axios from 'axios';

const login =
  function *login ({data}) {
    try {

      const response = yield axios.post('http://localhost:5000/login', data);
      if(response.data.isValid)
      {
        console.log(response.data.token);
        yield put(loginUserSuccess(response.data));
        if(response.data.user.complete === 4)
          yield put(push("/"));
        else
          yield put(push("/completeProfile"));
      }
      else 
      {
        yield put(loginErrorField(response.data.errorField))
      }
    }catch (error) {
      if (error.response) {
        yield put(loginError("error.response.statusText", "error.response.status"));
      }
    }
  };

export default function *() {
  yield takeLatest("LOGIN_USER", login);
}
