import {
    LOGIN_USER,
    LOGIN_USER_ERROR,
    LOGIN_USER_SUCCESS,
    LOGIN_USER_ERROR_USERNAME,
    LOGIN_USER_ERROR_PASSWORD
} from "../actions/loginAction";

export default function (state = {status: 'initialState',data :'idl'}, action) {
    switch (action.type) {
    case LOGIN_USER:
      return state.set( state.status , "loading");
    case LOGIN_USER_SUCCESS:
      return state.set(state.status,"success")
    case LOGIN_USER_ERROR:
      return state.set(state.status,"error")
      case LOGIN_USER_ERROR_USERNAME:
      return state.set(state.status,"Username Not Found")
      case LOGIN_USER_ERROR_PASSWORD:
      return state.set(state.status,"Password incorrect")
    default:
      return state;
    }
}
