import React from 'react'
import {connect} from "react-redux";
import {Route ,Switch,Redirect} from 'react-router-dom'
import RegisterContainer from './containers/Register'
import HomeContainer from './containers/Home'
import loginContainer from './containers/Login'
import NotFoundPage from './containers/NotFoundPage'
import ForgotPasswordContainer from './containers/ForgotPassword'
import EmailConfirmCont from './containers/emailConfirmation'
import ResetPasswordContainer from './containers/resetPassword'
import CompleteProfile from './containers/completeProfile/stepper'

const Routes = (props) => {

    const PrivateRoute = ({component: Component, ...rest}) => {
        return (
            <Route {...rest} render={props => (
                props.token ?
                    <Component {...props} />
                : <Redirect to="/login" />
            )} />
        );
    };
    const PublicRoute = ({component: Component, restricted, ...rest}) => {
        return (
            <Route {...rest} render={props => (
                props.token && restricted ?
                    <Redirect to="/" />
                : <Component {...props} />
            )} />
        );
    };

    return (
        <div>
            <Switch>
                <PublicRoute path="/confirmation/:token"  component={ EmailConfirmCont } restricted={true} exact/>
                <PublicRoute path="/resetPassword/:token" component={ ResetPasswordContainer } restricted={true} exact/>
                <PublicRoute path="/forgotPassword"  component={ ForgotPasswordContainer } restricted={true} exact/>
                <PublicRoute path="/register" component={ RegisterContainer } restricted={true} exact/>
                <PublicRoute path="/login" component={ loginContainer } restricted={true}  exact/>
                <PrivateRoute path="/completeProfile" component={ CompleteProfile } exact/>
                <PrivateRoute path="/" exact component={ HomeContainer } exact/>
                <PublicRoute  path="*" component={ NotFoundPage } restricted={true} />
            </Switch>
        </div>
    )
}

const mapStateToProps = (state) => (
{
    'token': state.token,
});

export default connect(mapStateToProps)(Routes);
