import {
    SEND_EMAIL,
    SEND_EMAIL_SUCCESS,
    SEND_EMAIL_ERROR
} from "../actions/sendEmail";

const DEFAULT_STATE = {
  status: 'initialState',
};

export default function (state = DEFAULT_STATE, action) {
    switch (action.type) {
      case EMAIL_CONFIRMATION:
        return "loading"
      case EMAIL_CONFIRMATION_SUCCESS:
      return "success"
      case EMAIL_CONFIRMATION_ERROR:
        return "error"
      default:
        return state;
    }
}
