import React from 'react'
import {connect} from "react-redux";
import {Route ,Switch,Redirect} from 'react-router-dom'
import RegisterContainer from './containers/Register'
import HomeContainer from './containers/Home'
import loginContainer from './containers/Login'
import NotFoundPage from './containers/NotFoundPage'
import ForgotPasswordContainer from './containers/ForgotPassword'
import EmailConfirmCont from './containers/emailConfirmation'
import ResetPasswordContainer from './containers/resetPassword'
import CompleteProfile from './containers/completeProfile/stepper'

const Routes = () => {
    return (
        <>
            <Switch>
                <PublicRoute path="/confirmation/:token"    component={ EmailConfirmCont }      restricted={true}   />
                <PublicRoute path="/resetPassword/:token"   component={ ResetPasswordContainer } restricted={true}  />
                <PublicRoute path="/forgotPassword"         component={ ForgotPasswordContainer } restricted={true} />
                <PublicRoute path="/register"               component={ RegisterContainer }     restricted={true}   />
                <PublicRoute path="/login"                  component={ loginContainer }        restricted={true}   />
                <PrivateRoute path="/completeProfile"       component={ CompleteProfile }                           />
                <PrivateRoute path="/"                      component={ HomeContainer }                             />
                <PublicRoute  path="*"                      component={ NotFoundPage }          restricted={false}/>
            </Switch>
        </>
    )
}

const mapStateToProps = (state) => (
{
    'token': state.token,
});

export default connect(mapStateToProps)(Routes);
