import React from 'react';
import Paper from '@material-ui/core/Paper';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import CssBaseline from '@material-ui/core/CssBaseline';
import Container from '@material-ui/core/Container';
import { Grid } from '@material-ui/core';
import Avatar from '@material-ui/core/Avatar';
import { makeStyles } from '@material-ui/core/styles';
import PlaceTwoToneIcon from '@material-ui/icons/PlaceTwoTone';
import AccountCircleTwoToneIcon from '@material-ui/icons/AccountCircleTwoTone';
const useStyles = makeStyles(theme => ({
  bigAvatar: {
    width: 150,
    height: 150,
  },
  container: {
    backgroundColor:'#CCCFDD',
  },
  root: {
    flexGrow: 1,
    maxWidth: 500,
  },
}));
export default function SimpleContainer() {
    const [value, setValue] = React.useState(0);
    const handleChange = (event, newValue) => {
        setValue(newValue);
    };
    const classes = useStyles();
    return (
    <React.Fragment>
        <CssBaseline />
            <Grid container  justify="center">
                <Grid item xs={5} xs={6}>
                    <Container  maxWidth="sm" className={classes.container}>
                        <Avatar alt="Remy Sharp" src="https://c4.wallpaperflare.com/wallpaper/200/466/543/monica-bellucci-actress-women-wallpaper-preview.jpg" className={classes.bigAvatar} />
                    </Container>    
                </Grid>
                <Grid item xs={6} xs={6}>
                    <Paper square className={classes.root}>
                        <Tabs
                            value={value}
                            onChange={handleChange}
                            variant="fullWidth"
                            indicatorColor="secondary"
                            textColor="secondary"
                            aria-label="icon label tabs example"
                        >

                            <Tab icon={<AccountCircleTwoToneIcon />} label="INFOS" />
                            <Tab icon={<PlaceTwoToneIcon />} label="LOCALISATION" />
                        </Tabs>
                    </Paper>
                </Grid>
            </Grid>
   </React.Fragment>
  );
}






