import React, {useState} from 'react';
import Picture from '../../components/completeProfile/pictures';
import {connect} from "react-redux";
import axios from 'axios';

export const Pictures = (props) => {
    const {userId} = props;
const [imagePreviewUrl,setImagePreviewUrl] = React.useState();
//const [message,setMessage] = React.useState(null);
const fileChangedHandler =  async  (event,userId) => {
    let files  = event.target.files[0];
    // if(files.types)
    // {
    //     console.log(files)
    // }
    let reader  = new FileReader();
    reader.onload = (event) => {
        setImagePreviewUrl(reader.result);
    }
    reader.readAsDataURL(files);
    const headers = {'Content-Type': 'multipart/form-data'};
    const formData = new FormData();
    formData.append('files',files);
    try{
      const response = await axios.post('http://localhost:5000/upload',{formData : formData, userId : userId},headers);
      console.log(response)
    } catch(err){

    }
}
//console.log('ddsd'+imagePreviewUrl)
    return (
        <div>
            <Picture fileChangedHandler = {fileChangedHandler} imagePreviewUrl = {imagePreviewUrl}/>
        </div>
    )
}


const mapStateToProps = (state) => (
{
    "userId" : state.user.id,
});
const mapDispatchToProps = {

};

export default connect(mapStateToProps, mapDispatchToProps)(Pictures);
